mapper.select:
select *
from manage_user where 1 = 1

  and username like concat('%',#{keyWord},'%')


  ORDER BY #{PageSort} #{PageAsc}
  limit #{PageStart},#{PageSize}

mapper.update:
update manage_user
set password=#{password}
where identityID = #{identityID }

update manage_user
set ware_id = 0
where ware_id = #{id,jdbcType=INTEGER}
