mapper.select:
select *
from ware

  ORDER BY #{PageSort} #{PageAsc}
  limit #{PageStart},#{PageSize}

select u.*
from manage_user u,
     ware w
where u.ware_id = w.id
  and w.id = #{id,jdbcType=VARCHAR}

select ware.name
from ware,
     manage_user
where ware.id = manage_user.ware_id
  and username = #{username,jdbcType=VARCHAR}
